import Layout from '../components/Layout';
import Link from 'next/link';
//import fetch from 'isomorphic-unfetch';

const key = 'XB3vY8VxHRq2gORl6Vs2VspjBmFj26eG1WD91SU09nWCQonR6L';
const secret = '3bPz0lHF0NqGt7OHuJeeWhgTLizUqcHqVuFJUGmL';
// dog filters
const size = 'large';
const type = 'dog';
const status = 'adoptable';
// token vars
var token, tokenType, expires;

//renders
const Index = props => (
  <Layout>
	<h1>Doggos</h1>
	{props.data}
  </Layout>
);

var getDoggos = function() {
  return fetch('https://api.petfinder.com/v2/animals?size=' + size + '&type=' + type + '&status=' + status, {
	headers: {
	  'Authorization': tokenType + ' ' + token,
	  'Content-Type': 'application/x-www-form-urlencoded'
	}
  }).then(function(response) {
	return response.json();
  }).then(function(data) {
	console.log('doggos', data);
  }).catch(function(err){
	console.log('something went wrong...', err);
  });
};

var getOAuth = function() {
  return fetch('https://api.petfinder.com/v2/oauth2/token', {
  method: 'POST',
  body: 'grant_type=client_credentials&client_id=' + key + '&client_secret=' + secret,
  headers: {
	'Content-Type': 'application/x-www-form-urlencoded'
	}
  }).then(function(response) {
	return response.json();
  }).then(function(data) {
	console.log('token', data);
	token = data.access_token;
	tokenType = data.token_type;
	expires = new Date().getTime() + (data.expires_in * 1000);
  }).catch(function(err) {
	console.log('something went wrong...', err);
  });
};  

var callDoggos = function() {
	var now = new Date();
	if (!expires || expires - now.getTime() < 1) {
		console.log('new call');
		getOAuth().then(function() {
			getDoggos();
		});
		return;
	}
		console.log('from cache');
	  	getDoggos();
};


Index.getInitialProps = async function() {
  const data = await callDoggos();

  console.log(data);

  return {
	doggos: data
  };
};

export default Index;
